import React from 'react'
import { useState } from 'react'

const App = () => {
     const [fullName,setFullName]=useState({
         fname:"",
         lname:"",
         
     })
    
    
    const InputEvent = (event) => {
        console.log(event.target.value)
        setFullName(event.target.value)
    }
    
    
    const onSubmits = (event) => {
        event.preventDefault();
        setFullName(fullName)
        alert("Form Submited")
        
        
    }
    return (
        <>
            <div className="main-div">

                <form onSubmit={onSubmits}>
                    <div>
                        <h1>Log-In {FullName.fname}</h1>
                        <input type="text" placeholder="Enter You First Name" onChange={InputEvent} name='fname' value={fullName.fname} />
                        <input type="text" placeholder="Enter You last Name"  onChange={InputEvent} name='lname' value={fullName.lname} />
                        <button type="submit">Submit</button>
                    </div>
                </form>
            </div>
        </>
    )
}
export default App;
