   // now this one for double clikedevent
   const green='#00FF00';
   const [dbbg,setDbbg]=useState(green)

   //this is change for name
   const name1='Cliked Me';
   const [name,setName]=useState(name1);
  function myFun1()
   {
        console.log("You Cliked ")
        let newBg='#FF0000'
        setBg(newBg);
        let newName='How Dare You Just Touch me'
        setName()
   }
    function myFun2(){
        console.log("You Double Cliked on me")
        let newDbBg='	#00FFFF';
        setDbbg(newDbBg);

    }

   return( 
   <>
   <div style={{backgroundColor:bg}}>
     <button onClick={myFun1} onDoubleClick={myFun2}>{name}</button>
    </div>
    </>
    )}
export default App;
